    func tableView(_ tableView: UITableView, cellForRowAt indexPath: IndexPath) -> UITableViewCell {

        let cell = tableView.dequeueReusableCell(withIdentifier: "WorkoutCell", for: indexPath)

        let mainView = cell.contentView.viewWithTag(1001) as UIView?

        let imgView = mainView?.viewWithTag(1002) as! UIImageView

        let lblTitle = mainView?.viewWithTag(1003) as! UILabel
        let lblDescription = mainView?.viewWithTag(1004) as! UILabel
        let btnInstruction = mainView?.viewWithTag(1005) as! UIButton
        let btnStart = mainView?.viewWithTag(1006) as! UIButton

        mainView?.setShadowToCellView()

        let dicWorkout = arrWorkout[indexPath.row]

        lblTitle.text = dicWorkout["Name"] as? String
        lblDescription.text = dicWorkout["Description"] as? String
        imgView.image = dicWorkout["image"] as? UIImage

        btnInstruction.addTarget(self, action: #selector(self.clickOnStart(sender:)), for: .touchUpInside)
        btnStart.addTarget(self, action: #selector(self.clickOnStart(sender:)), for: .touchUpInside)

        cell.selectionStyle = .none

        return cell
    }

